shader.fragment:
#version 450

#include "uniforms/transform.glsl"

layout(location = 0) in vec2 f_UV;
layout(location = 0) out vec4 out_Color;

layout(set = 1, binding = 0) uniform texture2D screenTex;
layout(set = 1, binding = 1) uniform texture2D extraTex;
layout(set = 1, binding = 2) uniform sampler texSampler;

void main()
{    
    vec3 color = texture(sampler2D(screenTex, texSampler), f_UV).rgb;

    //if(f_UV.x <= 0.2 && f_UV.y <= 0.2) {
    //    color = texture(sampler2D(extraTex, texSampler), f_UV * 5.0).rrr;
    //}

    out_Color = vec4(color, 1.0);
}
